 results['internet_connectivity'] = hasInternet;
 } catch (e) {
 results['internet_connectivity'] = false;
 results['internet_error'] = NetworkUtils.getNetworkErrorMessage(e);
 }

 // If no internet, don't bother with the rest of the tests
