+import 'package:firebase_core/firebase_core.dart';
+import 'package:cloud_firestore/cloud_firestore.dart';
+
+/// Utility class for diagnosing Firebase issues
+class FirebaseDiagnostics {
+ /// Check Firebase initialization status
+ static Future<Map<String, dynamic>> checkFirebaseStatus() async {
+ final Map<String, dynamic> results = {
+ 'initialized': false,
+ 'app_count': 0,
+ 'default_app_exists': false,
+ 'project_id': null,
+ 'errors': [],
+ };
+ 
+ try {
+ // Check if Firebase is initialized
+ results['app_count'] = Firebase.apps.length;
+ results['initialized'] = Firebase.apps.isNotEmpty;
+ 
+ if (Firebase.apps.isNotEmpty) {
+ try {
+ final defaultApp = Firebase.app();
+ results['default_app_exists'] = true;
+ results['project_id'] = defaultApp.options.projectId;
+ } catch (e) {
+ results['default_app_exists'] = false;
+ results['errors'].add('Default app error: $e');
+ }
+ }
+ } catch (e) {
+ results['errors'].add('Firebase check error: $e');
+ }
+ 
+ return results;
+ }
+ 
+ /// Check Firestore connectivity
+ static Future<Map<String, dynamic>> checkFirestoreConnectivity() async {
+ final Map<String, dynamic> results = {
+ 'connected': false,
+ 'collections': [],
+ 'errors': [],
+ };
+ 
+ try {
+ // Try to list collections
+ final collections = await FirebaseFirestore.instance.listCollections();
+ results['connected'] = true;
+ results['collections'] = collections.map((c) => c.id).toList();
+ } catch (e) {
+ results['connected'] = false;
+ results['errors'].add('Firestore connectivity error: $e');
+ 
+ // Check if it's a permission issue
+ if (e.toString().contains('PERMISSION_DENIED')) {
+ results['permission_denied'] = true;
+ results['errors'].add('Permission denied. Check Firebase security rules.');
+ }
+ }
+ 
+ return results;
+ }
+ 
+ /// Try to access specific collections to diagnose permissions
+ static Future<Map<String, dynamic>> checkCollectionAccess() async {
+ final Map<String, dynamic> results = {};
+ final collections = ['yoga_classes', 'classInstances', 'bookings'];
+ 
+ for (final collection in collections) {
+ try {
+ final snap = await FirebaseFirestore.instance.collection(collection).limit(1).get();
+ results[collection] = {
+ 'accessible': true,
+ 'document_count': snap.docs.length,
+ };
+ } catch (e) {
+ results[collection] = {
+ 'accessible': false,
+ 'error': e.toString(),
+ };
+ }
+ }
+ 
+ return results;
+ }
+ 
+ /// Run all diagnostics
+ static Future<Map<String, dynamic>> runAllDiagnostics() async {
+ final Map<String, dynamic> results = {};
+ 
+ results['firebase_status'] = await checkFirebaseStatus();
+ results['firestore_connectivity'] = await checkFirestoreConnectivity();
+ results['collection_access'] = await checkCollectionAccess();
+ 
+ return results;
+ }
+}","refToolCallId":"toolu_013EtgKPheL8pSG6MC4m7cCc"},{"type":"text","text":"Now, let's create a screen to display the Firebase diagnostics:"},{"type":"toolCall","toolCallId":"toolu_01AfHCLe3C3CX9ZwdgWUVLox","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"comp1786cw/lib/screens/firebase_diagnostic_screen.dart","fsPath":"/Users/macmini/AndroidStudioProjects/comp1786/comp1786cw/lib/screens/firebase_diagnostic_screen.dart"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- comp1786cw/lib/screens/firebase_diagnostic_screen.dart
+++ comp1786cw/lib/screens/firebase_diagnostic_screen.dart
