+import 'package:flutter/material.dart';
+import '../utils/firebase_diagnostics.dart';
+
+class FirebaseDiagnosticScreen extends StatefulWidget {
+ const FirebaseDiagnosticScreen({Key? key}) : super(key: key);
+
+ @override
+ State<FirebaseDiagnosticScreen> createState() => _FirebaseDiagnosticScreenState();
+}
+
+class _FirebaseDiagnosticScreenState extends State<FirebaseDiagnosticScreen> {
+ bool _isLoading = true;
+ Map<String, dynamic> _diagnosticResults = {};
+ String _errorMessage = '';
+
+ @override
+ void initState() {
+ super.initState();
+ _runDiagnostics();
+ }
+
+ Future<void> _runDiagnostics() async {
+ setState(() {
+ _isLoading = true;
+ _errorMessage = '';
+ });
+
+ try {
+ final results = await FirebaseDiagnostics.runAllDiagnostics();
+ setState(() {
+ _diagnosticResults = results;
+ _isLoading = false;
+ });
+ } catch (e) {
+ setState(() {
+ _isLoading = false;
+ _errorMessage = 'Failed to run diagnostics: $e';
+ });
+ }
+ }
+
