 try {
 final doc = await _db.collection('yoga_classes').doc(id).get();
 if (!doc.exists) return null;
 final c = Course.fromJson(doc.data()!);
 c.id = doc.id;
 return c;
 } catch (e) {
 print('Error fetching course by ID: $e');
 throw ApiException(NetworkUtils.getNetworkErrorMessage(e), 503);
 }
 }

