 /// Create a booking
 Future<Booking> createBooking(
 String email, List<String> classInstanceIds) async {
 if (classInstanceIds.isEmpty) {
 throw ApiException('No class instances provided', 400);
 }
 try {
 final data = {
 'email': email,
 'classInstanceIds': classInstanceIds,
 'bookingDate': FieldValue.serverTimestamp(),
 'totalPrice': 0.0, // This would typically be calculated based on class prices
 };
 final ref = await _db.collection('bookings').add(data);
 final b = Booking.fromJson({
 'id': ref.id,
 'email': email,
 'classInstanceIds': classInstanceIds,
 'bookingDate': DateTime.now().toIso8601String(),
 'totalPrice': 0.0,
 });
 return b;
 } catch (e) {
 print('Error creating booking: $e');
 throw ApiException(NetworkUtils.getNetworkErrorMessage(e), 503);
 }
 }

 /// Get bookings by email
 Future<List<Booking>> getBookingsByEmail(String email) async {
 try {
 final snap = await _db
 .collection('bookings')
 .where('email', isEqualTo: email)
 .get();
 return snap.docs.map((d) {
 // Create a map with the document data and add the id
 final data = d.data();
 data['id'] = d.id;

 // Convert Firestore Timestamp to ISO string if needed
 if (data['bookingDate'] != null && data['bookingDate'] is Timestamp) {
 data['bookingDate'] = (data['bookingDate'] as Timestamp).toDate().toIso8601String();
 }

 return Booking.fromJson(data);
 }).toList();
 } catch (e) {
 print('Error fetching bookings by email: $e');
 throw ApiException(NetworkUtils.getNetworkErrorMessage(e), 503);
 }
 }

 /// Diagnose network issues and return detailed diagnostic information
 Future<Map<String, dynamic>> diagnoseNetworkIssues() async {
 final Map<String, dynamic> results = {
 'api_mode': 'production',
 'api_base_url': 'https://yoga-api.example.com/api',
 };

 // Check internet connectivity
 bool hasInternet = false;
 try {
 hasInternet = await NetworkUtils.hasInternetConnection();
 results['internet_connectivity'] = hasInternet;
 } catch (e) {
 results['internet_connectivity'] = false;
 results['internet_error'] = NetworkUtils.getNetworkErrorMessage(e);
 }

 // If no internet, don't bother with the rest of the tests
 if (!hasInternet) {
 results['api_connectivity'] = false;
 results['api_error'] = 'No internet connection available';
 results['dns_resolution'] = false;
 results['dns_error'] = 'Cannot perform DNS lookup without internet';
 return results;
 }

 // Check DNS resolution
 try {
 final lookup = await InternetAddress.lookup('yoga-api.example.com');
 results['dns_resolution'] =
 lookup.isNotEmpty && lookup[0].rawAddress.isNotEmpty;
 results['resolved_ip'] = lookup.isNotEmpty ? lookup[0].address : null;
 } catch (e) {
 results['dns_resolution'] = false;
 results['dns_error'] = 'DNS lookup failed: ${e.toString()}';
 }

 // Check API connectivity
 try {
 final response = await http.get(
 Uri.parse('https://yoga-api.example.com/api/health'),
 headers: {'Accept': 'application/json'},
 ).timeout(const Duration(seconds: 10));

 results['api_connectivity'] =
 response.statusCode >= 200 && response.statusCode < 400;
 if (response.statusCode < 200 || response.statusCode >= 400) {
 results['api_error'] =
 'API returned status code: ${response.statusCode}';
 }
 } catch (e) {
 results['api_connectivity'] = false;
 results['api_error'] = NetworkUtils.getNetworkErrorMessage(e);
 }

 // Test a specific endpoint
 try {
 final response = await http.get(
 Uri.parse('https://yoga-api.example.com/api/classes'),
 headers: {'Accept': 'application/json'},
 ).timeout(const Duration(seconds: 10));

 results['endpoint_test'] = {
 'endpoint': '/api/classes',
 'success': response.statusCode >= 200 && response.statusCode < 400,
 'status_code': response.statusCode,
 };
 } catch (e) {
 results['endpoint_test'] = {
 'endpoint': '/api/classes',
 'success': false,
 'error': NetworkUtils.getNetworkErrorMessage(e),
 };
 }

 return results;
 }
