+ .collection('test_connection')
+ .get();
+ 
+ List<Map<String, dynamic>> documents = [];
+ for (var doc in snapshot.docs) {
+ documents.add({
+ 'id': doc.id,
+ 'data': doc.data(),
+ });
+ }
+ 
+ setState(() {
+ _isLoading = false;
+ _isSuccess = true;
+ _statusMessage = "Successfully connected to Firestore!";
+ _testData = documents;
