+ _isLoading = false;
+ _statusMessage = "Firestore connection failed: ${e.toString()}";
+ });
+ }
+ } catch (e) {
+ setState(() {
+ _isLoading = false;
+ _statusMessage = "Firebase initialization failed: ${e.toString()}";
+ });
+ }
+ }
+
+ Future<void> _writeTestDocument() async {
+ setState(() {
+ _isLoading = true;
+ _statusMessage = "Writing test document...";
