+/**
+ * Activity to test Firebase Firestore connection
+ */
+public class FirebaseTestActivity extends AppCompatActivity {
+ private static final String TAG = "FirebaseTestActivity";
+ private ActivityFirebaseTestBinding binding;
+ private FirebaseFirestore db;
+ private List<Map<String, Object>> testDocuments = new ArrayList<>();
+ private TestDocumentAdapter adapter;
+
+ @Override
+ protected void onCreate(Bundle savedInstanceState) {
+ super.onCreate(savedInstanceState);
+ binding = ActivityFirebaseTestBinding.inflate(getLayoutInflater());
+ setContentView(binding.getRoot());
+ setSupportActionBar(binding.toolbar);
+ getSupportActionBar().setDisplayHomeAsUpEnabled(true);
+ 
+ // Initialize Firestore
+ db = FirebaseFirestore.getInstance();
+ 
+ // Setup RecyclerView
+ adapter = new TestDocumentAdapter(this, testDocuments);
+ binding.recyclerView.setLayoutManager(new LinearLayoutManager(this));
+ binding.recyclerView.setAdapter(adapter);
+ 
+ // Setup add document button
+ binding.fab.setOnClickListener(view -> addTestDocument());
+ 
+ // Load test documents
+ loadTestDocuments();
+ }
+ 
+ private void loadTestDocuments() {
+ binding.progressBar.setVisibility(View.VISIBLE);
+ binding.statusText.setText("Loading test documents...");
+ 
+ db.collection("test_connection")
+ .get()
+ .addOnSuccessListener(queryDocumentSnapshots -> {
+ binding.progressBar.setVisibility(View.GONE);
+ testDocuments.clear();
+ 
+ for (QueryDocumentSnapshot document : queryDocumentSnapshots) {
+ Map<String, Object> docData = new HashMap<>();
+ docData.put("id", document.getId());
+ docData.put("data", document.getData());
+ testDocuments.add(docData);
+ }
+ 
+ adapter.notifyDataSetChanged();
+ 
+ if (testDocuments.isEmpty()) {
+ binding.emptyView.setVisibility(View.VISIBLE);
+ binding.recyclerView.setVisibility(View.GONE);
+ binding.statusText.setText("No test documents found. Try adding one.");
+ } else {
+ binding.emptyView.setVisibility(View.GONE);
+ binding.recyclerView.setVisibility(View.VISIBLE);
+ binding.statusText.setText("Successfully loaded " + testDocuments.size() + " test documents");
+ }
+ 
+ // Set success status
+ binding.statusIcon.setImageResource(R.drawable.ic_check_circle);
+ binding.statusIcon.setColorFilter(getResources().getColor(android.R.color.holo_green_dark, getTheme()));
+ })
+ .addOnFailureListener(e -> {
+ binding.progressBar.setVisibility(View.GONE);
+ binding.statusText.setText("Error: " + e.getMessage());
+ Log.e(TAG, "Error loading test documents", e);
+ 
+ // Set error status
+ binding.statusIcon.setImageResource(R.drawable.ic_error);
+ binding.statusIcon.setColorFilter(getResources().getColor(android.R.color.holo_red_dark, getTheme()));
+ });
+ }
+ 
+ private void addTestDocument() {
+ binding.progressBar.setVisibility(View.VISIBLE);
+ binding.statusText.setText("Adding test document...");
+ 
+ // Create a new document with a timestamp
+ Map<String, Object> docData = new HashMap<>();
+ docData.put("test", "true");
+ docData.put("timestamp", com.google.firebase.Timestamp.now());
+ docData.put("message", "Test document created from Android app");
+ 
+ db.collection("test_connection")
+ .add(docData)
+ .addOnSuccessListener(documentReference -> {
+ binding.progressBar.setVisibility(View.GONE);
+ binding.statusText.setText("Test document added with ID: " + documentReference.getId());
+ Toast.makeText(this, "Document added successfully", Toast.LENGTH_SHORT).show();
+ 
+ // Reload documents
+ loadTestDocuments();
+ })
+ .addOnFailureListener(e -> {
+ binding.progressBar.setVisibility(View.GONE);
+ binding.statusText.setText("Error adding document: " + e.getMessage());
+ Toast.makeText(this, "Error adding document", Toast.LENGTH_SHORT).show();
+ Log.e(TAG, "Error adding document", e);
+ 
+ // Set error status
+ binding.statusIcon.setImageResource(R.drawable.ic_error);
+ binding.statusIcon.setColorFilter(getResources().getColor(android.R.color.holo_red_dark, getTheme()));
+ });
+ }
+ 
+ @Override
+ public boolean onOptionsItemSelected(@NonNull MenuItem item) {
+ if (item.getItemId() == android.R.id.home) {
+ onBackPressed();
+ return true;
+ }
