+ return new ViewHolder(view);
+ }
+
+ @Override
+ public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
+ Map<String, Object> document = documents.get(position);
+ 
+ // Set document ID
+ holder.documentIdText.setText("Document ID: " + document.get("id"));
+ 
+ // Set document data
+ Map<String, Object> data = (Map<String, Object>) document.get("data");
+ if (data != null) {
+ StringBuilder dataString = new StringBuilder();
+ for (Map.Entry<String, Object> entry : data.entrySet()) {
+ dataString.append(entry.getKey())
+ .append(": ")
+ .append(entry.getValue())
+ .append("\n");
+ }
+ holder.documentDataText.setText(dataString.toString());
+ } else {
+ holder.documentDataText.setText("No data");
+ }
+ }
+
+ @Override
+ public int getItemCount() {
+ return documents.size();
+ }
