+ 'description': description,
+ 'dayOfWeek': dayOfWeek,
+ 'time': time,
+ 'capacity': capacity,
+ 'duration': duration,
+ 'price': price,
+ 'classInstanceIds': classInstanceIds,
+ 'additionalFields': additionalFields,
